import React from 'react';

const MovieSearchAPI = () => {
    const [movie, setMovie] = useState([]);
    const [query, setQuery] = useState();
    const queryDebounce = useDebounce(query, 500);
    // console.log(queryDebounce);
    const [loading, setLoading] = useState(true);
    useEffect(() => {
      async function getData() {
        setLoading(true);
        try {
          const response = await axios.get(
            `https://api.themoviedb.org/3/search/movie?api_key=647c2db1d376cfd9a87f5a11d1108e6b&query='${queryDebounce}'`
          );
          if (response.data.results) {
            setMovie(response.data.results);
            console.log(response.data.results);
            setLoading(false);
          }
        } catch (error) {
          console.log(error);
        }
      }
      getData();
    }, [queryDebounce]);
    return (
        <div>
        <div className="text-center">
          <input
            className="border border-[#ccc] py-[10px] px-[20px] w-[400px]"
            type="search"
            name="search"
            id=""
            placeholder="Search movie..."
            onChange={(e) => setQuery(e.target.value)}
          />
        </div>
        {loading && (
          <div className="grid grid-cols-3 gap-10 mt-[50px]">
            <MovieLoading></MovieLoading>
            <MovieLoading></MovieLoading>
            <MovieLoading></MovieLoading>
          </div>
        )}
        <div className="grid grid-cols-3 gap-10 mt-[50px] ">
          {!loading &&
            movie?.map((item) => (
              <MovieItem key={item.id} props={item}></MovieItem>
            ))}
        </div>
      </div>
    );
};

export default MovieSearchAPI;
